mapper.update:
UPDATE point
SET usedPoint = #{usedPoint}, maxPoint = #{maxPoint}, grade = #{grade}
WHERE pointId = #{pointId}

mapper.select:
SELECT pointId, usedPoint, maxPoint, memId, grade
FROM point
WHERE memId = #{memId}

SELECT pointId, usedPoint, maxPoint, memId, grade
FROM point
WHERE memId = #{memId}
